            message = f"Read {post.title} at {post_url}\n\n{cd['name']}\'s comments: {cd['comments']}"
            send_mail(subject, message, from_email=None, recipient_list=[cd['to']])
            sent = True
        else:
            form = EmailPostForm()
    return render(
        request,
        'blog/post/share.html',
         {'post': post, 'form': form, 'sent': sent}
         )
